mapper.select:
select  
from CtText t 
where t.id = #{id}


select DATE_FORMAT( tradetime_, '%Y%m%d' ) as date, count(id_) as ids,
sum(amount_) as amount, count(user_id) as uids from trade_detail
group by date(tradetime_);

select DATE_FORMAT( tradetime_, '%Y%m%d' ) as date, count(id_) as ids,
sum(amount_) as amount, count(user_id) as uids from trade_detail
group by week(tradetime_);
